import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [countries, setCountries] = useState([]);
  const [error, setError] = useState(null);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const fetchCountries = () => {
    axios.get('https://restcountries.com/v3.1/all')
      .then(response => {
        const data = response.data;
        const filteredCountries = data.filter(country => {
          return country.name.common.toLowerCase().includes(searchTerm.toLowerCase());
        });
        setCountries(filteredCountries);
        setError(null);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setError('Error fetching data. Please try again later.');
      });
  };

  const openCountryWindow = (country) => {
    const countryWindow = window.open('', '_blank');
    countryWindow.document.write(`
      <html>
      <head>
        <title>${country.name.common}</title>
      </head>
      <body>
        <h1>${country.name.common}</h1>
        <img src="${country.flags.png}" alt="Flag" width="200" />
        <p>Population: ${country.population}</p>
      </body>
      </html>
    `);
  };

  return (
    <div className="App">
      <header>
        <img src="https://e7.pngegg.com/pngimages/680/534/png-clipart-logo-design-emblem-logo.png" alt="Logo" className="logo" />
        <input
          type="text"
          placeholder="Search for a country..."
          value={searchTerm}
          onChange={handleInputChange}
        />
        <button onClick={fetchCountries} className="firstbutton">Search</button>
      </header>

      {error && <p>{error}</p>}

      <ul>
        {countries.map(country => (
          <li key={country.name.common} onClick={() => openCountryWindow(country)}>
            {country.name.common}
          </li>
        ))}
      </ul>

      <footer>
        <div className="newsletter">
          <input type="email" placeholder="Email Address" />
          <button className="secondbutton">Subscribe</button>
        </div>

        <p>Privacy policy</p>
        <p>Cookies policy</p>
        <p>Terms of use</p>
      </footer>
    </div>
  );
}

export default App;
