import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchCountries();
  }, []); // Fetch countries on initial render

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const fetchCountries = () => {
    axios.get('https://restcountries.com/v3.1/all')
      .then(response => {
        const data = response.data;
        setCountries(data);
        setError(null);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setError('Error fetching data. Please try again later.');
      });
  };

  const handleCountryClick = (country) => {
    setSelectedCountry(country);
  };

  const handleLogoClick = () => {
    setSelectedCountry(null);
  };

  const filteredCountries = countries.filter(country => {
    return country.name.common.toLowerCase().includes(searchTerm.toLowerCase());
  });

  return (
    <div className="App">
      <header className="header">
        <span className="logo" onClick={handleLogoClick}>LOGO</span>
        <div className="input-container">
          <input
            type="text"
            placeholder="Search for a country..."
            value={searchTerm}
            onChange={handleInputChange}
          />
          <button onClick={fetchCountries} className="search-button">Refresh</button>
        </div>
      </header>

      {error && <p>{error}</p>}

      <div className="middle-section">
        {selectedCountry ? (
          <div className="country-info">
            <h2>{selectedCountry.name.common}</h2>
            <img src={selectedCountry.flags.png} alt="Flag" className="country-flag" />
            <p>Population: {selectedCountry.population}</p>
            <p>Capital: {selectedCountry.capital}</p>
            <p>Currency: {Object.keys(selectedCountry.currencies)[0]}</p>
            <button onClick={() => setSelectedCountry(null)}>Back to List</button>
          </div>
        ) : (
          <div className="countries-list">
            {filteredCountries.map(country => (
              <div key={country.name.common} className="country-card" onClick={() => handleCountryClick(country)}>
                <img src={country.flags.png} alt="Flag" className="country-flag" />
                <p className="country-name">{country.name.common}</p>
              </div>
            ))}
          </div>
        )}
      </div>

      <footer className="footer">
        <div className="newsletter">
          <input type="email" placeholder="Email Address" />
          <button className="subscribe-button">Subscribe</button>
        </div>

        <div className="legal-links">
          <p>Privacy policy</p>
          <p>Cookies policy</p>
          <p>Terms of use</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
