import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './.css';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [countries, setCountries] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchCountries();
  }, []); // Fetch countries on initial render

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const fetchCountries = () => {
    axios.get('https://restcountries.com/v3.1/all')
      .then(response => {
        const data = response.data;
        setCountries(data);
        setError(null);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setError('Error fetching data. Please try again later.');
      });
  };

  const openCountryWindow = (country) => {
    const countryWindow = window.open('', '_blank');
    countryWindow.document.write(`
      <html>
      <head>
        <title>${country.name.common}</title>
      </head>
      <body>
        <h1>${country.name.common}</h1>
        <img src="${country.flags.png}" alt="Flag" width="200" />
        <p>Population: ${country.population}</p>
        <p>Capital: ${country.capital}</p>
        <p>Currency: ${Object.keys(country.currencies)[0]}</p>
      </body>
      </html>
    `);
  };

  const filteredCountries = countries.filter(country => {
    return country.name.common.toLowerCase().includes(searchTerm.toLowerCase());
  });

  return (
    <div className="App">
      <header className="header">
        <img src="https://e7.pngegg.com/pngimages/680/534/png-clipart-logo-design-emblem-logo.png" alt="Logo" className="logo" />
        <div className="input-container">
          <input
            type="text"
            placeholder="Search for a country..."
            value={searchTerm}
            onChange={handleInputChange}
          />
          <button onClick={fetchCountries} className="search-button">Refresh</button>
        </div>
      </header>

      {error && <p>{error}</p>}

      <div className="middle-section">
        {searchTerm === '' ? (
          <div className="about-website">
            <h2>Welcome to My Website</h2>
            <p>This website provides information about countries around the world. Use the search bar above to find information about specific countries.</p>
          </div>
        ) : (
          <div className="countries-list">
            {filteredCountries.map(country => (
              <div key={country.name.common} className="country-card" onClick={() => openCountryWindow(country)}>
                <img src={country.flags.png} alt="Flag" className="country-flag" />
                <p className="country-name">{country.name.common}</p>
              </div>
            ))}
          </div>
        )}
      </div>

      <footer className="footer">
        <div className="newsletter">
          <input type="email" placeholder="Email Address" />
          <button className="subscribe-button">Subscribe</button>
        </div>

        <div className="legal-links">
          <p>Privacy policy</p>
          <p>Cookies policy</p>
          <p>Terms of use</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
